import React, { useState, useEffect } from 'react';
import io from 'socket.io-client';

const socket = io('http://localhost:4000');

const Game = ({ username, roomId }) => {
  const [gameState, setGameState] = useState('waiting'); // 'guessing', 'drawing', 'ended'
  const [word, setWord] = useState('');
  const [drawer, setDrawer] = useState('');
  const [round, setRound] = useState(0);
  const [totalRounds, setTotalRounds] = useState(0);
  const [leaderboard, setLeaderboard] = useState([]);
  const [guess, setGuess] = useState('');
  const [chat, setChat] = useState([]);
  const [users, setUsers] = useState([]);

  useEffect(() => {
    socket.emit('joinRoom', { roomId, username });

    socket.on('yourTurnToDraw', (word) => {
      setWord(word);
      setGameState('drawing');
    });

    socket.on('startRound', ({ drawer, round, totalRounds }) => {
      setDrawer(drawer);
      setRound(round);
      setTotalRounds(totalRounds);
      setWord('');
      setGameState('guessing');
    });

    socket.on('guessResult', ({ success }) => {
      if (success) alert('Correct guess!');
    });

    socket.on('chatMessage', (data) => {
      setChat((prev) => [...prev, data]);
    });

    socket.on('systemMessage', (msg) => {
      setChat((prev) => [...prev, { username: 'System', message: msg }]);
    });

    socket.on('gameOver', (lb) => {
      setLeaderboard(lb);
      setGameState('ended');
    });

    socket.on('updateUsers', (userList) => {
      setUsers(userList);
    });

    return () => {
      socket.off('yourTurnToDraw');
      socket.off('startRound');
      socket.off('guessResult');
      socket.off('chatMessage');
      socket.off('systemMessage');
      socket.off('gameOver');
      socket.off('updateUsers');
    };
  }, [roomId, username]);

  const sendGuess = () => {
    if (!guess.trim()) return;
    socket.emit('chatMessage', { username, message: guess, roomId });
    setGuess('');
  };

  const handleStartGame = () => {
    socket.emit('startGame', roomId);
  };

  return (
    <div className="p-4">
      <h2 className="text-xl font-bold">Room: {roomId}</h2>
      {gameState === 'drawing' && <p>You are drawing! Your word: <b>{word}</b></p>}
      {gameState === 'guessing' && <p>{drawer} is drawing! Round {round} of {totalRounds}</p>}
      {gameState === 'ended' && (
        <div>
          <h3 className="text-lg font-bold">Game Over!</h3>
          <ul>
            {leaderboard.map((user, i) => (
              <li key={i}>{user.username}: {user.score} pts</li>
            ))}
          </ul>
        </div>
      )}

      <div className="mt-4">
        <button onClick={handleStartGame} className="bg-blue-500 text-white px-3 py-1 rounded">
          Start Game
        </button>
      </div>

      {gameState === 'guessing' && (
        <div className="mt-4">
          <input
            type="text"
            value={guess}
            onChange={(e) => setGuess(e.target.value)}
            className="border p-1"
          />
          <button onClick={sendGuess} className="ml-2 px-2 py-1 bg-green-500 text-white rounded">
            Send
          </button>
        </div>
      )}

      <div className="mt-4">
        <h4 className="font-semibold">Chat:</h4>
        <div className="border p-2 h-40 overflow-y-scroll">
          {chat.map((msg, i) => (
            <div key={i}><strong>{msg.username}:</strong> {msg.message}</div>
          ))}
        </div>
      </div>

      <div className="mt-4">
        <h4 className="font-semibold">Players:</h4>
        <ul>
          {users.map((u, i) => (
            <li key={i}>{u.username}</li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default Game;
